import React, { Component } from 'react';

class App extends Component {
    render() {
        return(
            <div className = "App">
            <div>
            Add an Item...
            <br/>
            <input
            type="text"
            placeholder="Type item here...."
            value = {this.state.newItem}
            onChange={e => this,updateInput("newItem", e.target.value)}
            />
            <button
            onClick = {() => this.addItem()}>

            Add

            </button>

            </div>
            </div>
        );
    }
}

export default App;
